import React, { useState, useEffect } from 'react';
import Sidebar from '../Sidebar';
import Content from '../Content';
import Description from '../Description';

const Index = () => {
    const [open, setOpen] = useState(true)

    const size = useWindowSize();

    function useWindowSize() {

        const [windowSize, setWindowSize] = useState({
            width: undefined
        });

        useEffect(() => {
            const handleResize = () =>
                setWindowSize({ width: window.innerWidth });

            window.addEventListener('resize', handleResize);

            handleResize();

            return () => {
                window.removeEventListener('resize', handleResize);
            };
        }, []);

        return windowSize;
    };

    useEffect(() => {
        size.width < 1200 ? setOpen(false) : setOpen()
    }, [size, setOpen]);

    return (
        <div className='layout'>
            <div className='container'>
                <div className='row'>
                    <div className='col-xl-2 col-xxl-2'>
                        <Sidebar open={open} setOpen={setOpen} size={size}/>
                    </div>
                    <div className='col-lg-7 col-xl-6 col-xxl-6'>
                        <Content open={open} setOpen={setOpen}/>
                    </div>
                    <div className='col-lg-5 col-xl-4 col-xxl-4'>
                        <Description />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Index
